<style>body {margin: 0;}.cube-container {width: 100vw;height: 100vh;display: flex;align-items: center;justify-content: center;perspective: 1000px;}div.cube {width: 150px;height: 150px;position: relative;transform-style: preserve-3d;animation: rotateCubes 5s infinite ease;}.cube .face {position: absolute;width: 150px;height: 150px;background: transparent;border: 2px solid red;background-size: cover;border-radius: 50%;background-color: #ffffff;overflow: hidden;}.cube .face:nth-child(1) {transform: rotateX(0deg) translateZ(75px);background-image: url(assets/public_images/loading-1.jpg);}.cube .face:nth-child(2) {transform: rotateX(90deg) translateZ(75px);background-image: url(assets/public_images/loading-2.jpg);}.cube .face:nth-child(3) {transform: rotateX(180deg) translateZ(75px) rotateZ(90deg);background-image: url(assets/public_images/loading-4.jpg);}.cube .face:nth-child(4) {transform: rotateX(-90deg) translateZ(75px) rotateZ(90deg);background-image: url(assets/public_images/loading-5.jpg);}.cube .face:nth-child(5) {transform: rotateY(-90deg) translateZ(75px) rotateZ(-90deg);background-image: url(assets/public_images/loading-3.jpg);}.cube .face:nth-child(6) {transform: rotateY(90deg) translateZ(75px);background-image: url(assets/public_images/loading-6.jpg);}@keyframes rotateCubes {0% {transform: rotateX(0deg) rotateZ(0deg);scale: 1.6;}5% {scale: 2;}10% {transform: rotateX(0deg) rotateZ(0deg);scale: 1.4;}25% {transform: rotateX(-90deg) rotateZ(0deg);}40% {transform: rotateX(-90deg) rotateZ(90deg);}55% {transform: rotateX(-180deg) rotateZ(90deg);}70% {transform: rotateX(-270deg) rotateY(-90deg) rotateZ(0deg);}85% {transform: rotateX(-360deg) rotateY(-90deg) rotateZ(0deg);}100% {transform: rotateX(-360deg) rotateZ(0deg) rotateZ(0deg);scale: 1.4;}}</style><div id="loading-overlay"><div class="cube-container"><div class="cube"><i class="face"></i><i class="face"></i><i class="face"></i><i class="face"></i><i class="face"></i><i class="face"></i></div></div></div><script>document.addEventListener("DOMContentLoaded",function(){hideLoader()});function hideLoader(){var e=document.getElementById("loading-overlay");e.style.opacity=0,e.style.display="none"}</script>